-- ----------------------------
-- Table structure for t_article
-- ----------------------------
DROP TABLE IF EXISTS `t_article`;
CREATE TABLE `t_article`  (
  `bw_id` bigint(9) NOT NULL AUTO_INCREMENT COMMENT '文章id',
  `bw_title` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章标题',
  `u_id` bigint(20) NOT NULL COMMENT '文章作者id',
  `bw_desc` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章简介',
  `kwd_id` bigint(20) NULL DEFAULT NULL COMMENT '关键词id',
  `bw_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章内容',
  `bw_click` int(10) NOT NULL DEFAULT 0 COMMENT '点击数',
  `bw_state` tinyint(1) NOT NULL DEFAULT 0 COMMENT '0:不推荐 1：推荐',
  `cate_id` bigint(9) NOT NULL COMMENT '所属栏目ID',
  `bw_is_top` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '1：置顶，0：默认不置顶',
  `deleted` tinyint(1) NULL DEFAULT NULL,
  `create_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_date` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`bw_id`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 18 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '博文表' ROW_FORMAT = Dynamic;

